import {useEffect,useState} from "react";
import supabase from './supabase';
import "./style.css";

const initialFacts = [
  {
    id: 1,
    text: "React is being developed by Meta (formerly facebook)",
    source: "https://opensource.fb.com/",
    category: "technology",
    votesInteresting: 24,
    votesMindblowing: 9,
    votesFalse: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "Millennial dads spend 3 times as much time with their kids than their fathers spent with them. In 1982, 43% of fathers had never changed a diaper. Today, that number is down to 3%",
    source:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "society",
    votesInteresting: 11,
    votesMindblowing: 2,
    votesFalse: 0,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "Lisbon is the capital of Portugal",
    source: "https://en.wikipedia.org/wiki/Lisbon",
    category: "society",
    votesInteresting: 8,
    votesMindblowing: 3,
    votesFalse: 1,
    createdIn: 2015,
  },
];

const CATEGORIES = [
  { name: "technology", color: "#3b82f6" },
  { name: "science", color: "#16a34a" },
  { name: "finance", color: "#ef4444" },
  { name: "society", color: "#eab308" },
  { name: "entertainment", color: "#db2777" },
  { name: "health", color: "#14b8a6" },
  { name: "history", color: "#f97316" },
  { name: "news", color: "#8b5cf6" },
];
/*
function Counter(){
  let [count,setCount]=useState(0);
  return (
    <div>
      <span style={{fontSize:'40px'}}>{count}</span>
      <button className="btn btn-all-categories" onClick={()=>{setCount((c)=>c+1)}}>+1</button>
    </div>
  );
}*/

function App() {
  const [showForm,setShowForm]=useState(false);
  const [facts,setFacts]=useState([]);
  const [isLoading,setIsLoading]=useState(false);
  const [currentCategory,setCurrentCategory]=useState("all");

  useEffect(function() {
    async function getFacts(){
      setIsLoading(true);

      let query=supabase.from("facts").select("*");

      if(currentCategory!=="all") query =query.eq("category",currentCategory);


    let { data: facts, error } = await query
   .order("votesInteresting",{ascending:false}).limit(1200);

  if(!error) setFacts(facts);
  else alert("there was a problem getting data");
   setIsLoading(false);
      }
      getFacts();
      },[currentCategory]);

  return (
    <>
    <Header showForm={showForm} setShowForm={setShowForm} />
    {showForm?<NewFactForm setFacts={setFacts} setShowForm={setShowForm}/> :null}


      <main className="main">
      <CategoryFilter setCurrentCategory={setCurrentCategory}/>  
        {isLoading ?<Loader />:<FactsList facts={facts} setFacts={setFacts}/>}

      </main>
      </>
  );
}

function Loader(){
  return <p className="message">loading....</p>;
}

function Header({showForm,setShowForm}){
  return (
    <header className="header" >  
    <div className="logo">
    <img src="logo.png" height="68" width="68" alt=""/>
    <h1>Today I Learned</h1>
    </div>
    <button className="btn btn-open" onClick={()=>setShowForm((show)=>!show)}>{showForm?'close':'Share a Fact'}</button>
    </header>

  );
}

function isValidHttpUrl(string) {
  let url;

  try {
    url = new URL(string);
  } catch (_) {
    return false;  
  }

  return url.protocol === "http:" || url.protocol === "https:";
}



function NewFactForm({setFacts,setShowForm}){
  const [text,setText]=useState("");
  const [source,setSource]=useState("");
  const [category,setCategory]=useState("");
  const [isUploading,setIsUploading]=useState(false);
  const textLength=text.length;

 async function handleSubmit(e){
    //1.prvent browser load
    e.preventDefault();
    //console.log(e);
    //2.check validity of data
    if(text && isValidHttpUrl(source) && category && textLength<200){
  //3,create a fact 
  /*const newFact=
  {
    id:Math.round(Math.random()*10000000),
    text ,
    source ,
    category ,
    votesInteresting: 0,
    votesMindblowing: 0,
    votesFalse: 0,
    createdIn: new Date().getFullYear(),
  };*/
  //3.upload the fact toi superbase and receive the new fact object 
  setIsUploading(true);
const{data:newFact,error}= await supabase.from('facts').insert([{text,source,category}]).select();
console.log(newFact);

setIsUploading(false);
   //4.add fact to the ui through state
   if(!error) setFacts((facts)=>[newFact[0],...facts]);
    //5.reset all input fields 
    setText("");
    setSource("");
    setCategory("");
      //6 close form
    setShowForm(false);
    }
    else{
      alert("give valid input");
    }
  }

  return (
  <form className="fact-form" onSubmit={handleSubmit}>
  <div className="inner-fact">    
  <input type="text" placeholder="Share a fact with world..."
      value={text}
      onChange={(e)=>setText(e.target.value)}
      disabled={isUploading}
  />
  <span>{200-textLength}</span>
  <input type="text" placeholder="Trust worthy source"
    value={source}
    onChange={(e)=>{setSource(e.target.value)}}
    disabled={isUploading}
  />
  <select value={category} onChange={(e)=>setCategory(e.target.value)} disabled={isUploading}>
      <option value="">Choose category:</option>
      {CATEGORIES.map((categor)=>(
        <option key={categor.name} value={categor.name}>{categor.name.toUpperCase()}</option>
      ))}

  </select>
</div>

  <button className="btn post-btn" disabled={isUploading}>POST</button>
  </form>);
}

function CategoryFilter({setCurrentCategory}){
    return ( 
    <aside>
      <ul>
      <li className='category'><button className="btn btn-all-categories" onClick={()=>setCurrentCategory("all")}>All</button></li>
        {CATEGORIES.map((cat)=> (<li key={cat.name} className="category">
          <button className="btn btn-category" style={{backgroundColor:cat.color}}
          onClick={()=>setCurrentCategory(cat.name)}>
          {cat.name}
          </button>
          </li>))}

      </ul>
    </aside>

    );
}

function FactsList({facts, setFacts}){
  if(facts.length ===0){
    return <p className="message">No facts for this  category yet</p>;
  }

  return (
    <section className="section">
    <ul className="facts-list">
      {facts.map((fact)=>(
          <Fact key={fact.id} fact={fact} setFacts={setFacts}/>
      ))}
    </ul>
    </section>
  )
}

function Fact({fact,setFacts}){
  const [isUpdating, setIsUpdating] = useState(false);
  const isDisputed =
    fact.votesInteresting + fact.votesMindBlowing < fact.votesFalse;
  async function handleVote(columnName){
    setIsUpdating(true);
    const {data:updatedFact,error}=await supabase.from('facts').update({
      [columnName]: fact[columnName] + 1
    }).eq("id",fact.id).select();
    setIsUpdating(false);
    if (!error)
      setFacts((facts) =>
        facts.map((f) => (f.id === fact.id ? updatedFact[0] : f))
      );

  }

  return (

    <li  className="fact"> 
          <p>
          {isDisputed ? <span className='disputed'>[‚õîÔ∏è DISPUTED]</span> : null}
            {fact.text}
              <a href={fact.source} className="source" target='_blank'>(Source)</a>
          </p>
      <div className="tag-div">    <span className ="tag" style={{backgroundColor:CATEGORIES.find((cat)=>cat.name===fact.category).color,}}>{fact.category}</span></div>
          <div className="vote-buttons"> 
              <button onClick={() => handleVote('votesInteresting')}
          disabled={isUpdating}>üëç{fact.votesInteresting}</button>
              <button onClick={() => handleVote('votesMindBlowing')}
          disabled={isUpdating}>ü§Ø{fact.votesMindBlowing}</button>
              <button onClick={() => handleVote('votesFalse')} disabled={isUpdating}>‚õîÔ∏è{fact.votesFalse}</button>
          </div>   
  </li> 
  )
}


export default App;
